mapper.insert:
insert into personal(
    user_id,
    questions_id,
    teach_id
)values (
    #{userId},
    #{questionsId},
    #{teachId}
)

mapper.select:
select
    questions_start questionsStart,
    questions_start_image questionsStartImage
from questions
where grade = #{grade}
    and course = #{course}
    and complexity = #{complexity}
    and questions_type = #{questionsType}
    and year = #{year}
    and label = #{label}
    and knowledge = #{knowledge}
