mapper.insert:
insert into personal(
    user_id,

        questions_id,

    teach_id
)values (
    #{userId},

        #{questionsId},

    #{teachId}
) on duplicate key  update update_time = CURRENT_TIMESTAMP

mapper.update:
update teach
set is_del = 1
where id = #{teachId}

mapper.select:
select t.id id, t.teach_phone teachPhone, t.teach_name teachName, t.prepare_lessons_time prepareLessonsTime, t.course course, t.grade grade, t.student_name, t.lessons_time lessonsTime, t.teach_topic teachTopic, t.teach_type teachType, t.teach_source teachSource, t.student_questions studentQuestions, t.teach_goal teachGoal, t.teach_key_difficulty teachKeyDifficulty, t.review review, t.key_point keyPoint, t.senior_question_ids seniorQuestionIds, t.conclude conclude, t.consolidate consolidate, t.consolidate_question_ids consolidateQuestionIds, t.student_name studentName from teach t
inner join personal p on p.teach_id = t.id
where t.is_del = 0
and t.grade = #{grade}
and t.course = #{course}
and t.prepare_lessons_time in
#{g}
and t.teach_topic in
#{g}
and p.user_id = #{userId}
